mapper.select:
SELECT 

FROM oa_notify a

WHERE a.id = #{id}

SELECT
, r.read_flag
FROM oa_notify a
JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.TYPE = #{type}
AND a.STATUS = #{status}
and r.read_flag = #{readFlag}
AND a.STATUS = '1'
AND a.TYPE != '5'
and r.read_flag = '0'
ORDER BY a.update_date DESC

SELECT
, r.read_flag
FROM oa_notify a
JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.TYPE !='4' AND a.TYPE !='5'
AND a.TYPE = #{type}
and r.read_flag = #{readFlag}
AND a.STATUS = '1'
and r.read_flag = '0'
ORDER BY a.update_date DESC

SELECT
FROM oa_notify a
WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.create_by=#{createBy.id}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.TYPE = #{type}
AND a.STATUS = #{status}
and a.type != '5' and a.type != '4' ORDER BY a.update_date DESC

SELECT 
	count(1)
FROM oa_notify a

	JOIN oa_notify_record r ON r.oa_notify_id = a.id AND r.user_id = #{currentUser.id}

			AND r.read_flag = #{readFlag}


WHERE a.del_flag = #{DEL_FLAG_NORMAL}

	AND a.STATUS = '1'

mapper.update:
UPDATE oa_notify SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE oa_notify SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE keyid = #{keyId}

UPDATE oa_notify SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE content = #{content}
