mapper.select:
SELECT 

FROM oa_contract_purchase a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.contract_type='1'

		AND a.contract_no LIKE 
			'%'||#{contractNo}||'%'
			'%'+#{contractNo}+'%'
			concat('%',#{contractNo},'%')


		AND a.contract_name LIKE 
			'%'||#{contractName}||'%'
			'%'+#{contractName}+'%'
			concat('%',#{contractName},'%')


		AND a.contract_status = #{contractStatus}


		AND a.project_id = #{project.id}


		AND a.contract_time>=#{beginContractTime} 


		AND a.contract_time<=#{endContractTime} 


		AND a.supplier_id = #{supplier.id}

	${sqlMap.isOverdue}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_contract_purchase SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
