mapper.sql:
		SELECT 
        	doc_id,lend_status,lend_user_id
    	FROM
        	oa_doc_read_apply AS odra
    	WHERE del_flag=#{DEL_FLAG_NORMAL}
    	AND lend_user_id=#{loanUserId}

mapper.select:
SELECT 
	COUNT(a.id) as listCount
FROM oa_doc a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.keyword LIKE 
			'%'||#{keyword}||'%'
			'%'+#{keyword}+'%'
			concat('%',#{keyword},'%')


		AND a.office_id = #{office.id}


		AND a.is_public = #{isPublic}


		AND a.is_need_lend = #{isNeedLend}


		AND a.doc_type = #{docType}


		AND a.create_by = #{currentUserId}

SELECT 

FROM oa_doc a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.keyword LIKE 
			'%'||#{keyword}||'%'
			'%'+#{keyword}+'%'
			concat('%',#{keyword},'%')


		AND a.office_id = #{office.id}


		AND a.is_public = #{isPublic}


		AND a.is_need_lend = #{isNeedLend}


		AND a.doc_type = #{docType}


		AND a.create_by = #{currentUserId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_doc a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_doc SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cy_tsp_oa_address_group SET 
	del_flag = '1' 
WHERE group_id = #{groupId}
