mapper.select:
SELECT
COUNT(a.id)
FROM oa_mail_record a
WHERE a.mail_id = #{mailid}
AND a.create_by = #{userid}

SELECT b.name AS "createBy.name", c.name AS "createBy.office.name",

FROM oa_mail a


WHERE a.id = #{id}

SELECT

FROM oa_mail a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE
		'%'||#{title}||'%'
		'%'+#{title}+'%'
		concat('%',#{title},'%')


		AND a.create_by = #{createBy.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM oa_mail a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_mail SET
isdraft=#{isdraft}
WHERE id = #{id}

UPDATE oa_mail SET
del_flag = #{DEL_FLAG_DELETE}
WHERE
id = #{id}
