mapper.select:
SELECT 

FROM oa_project a

WHERE a.id = #{id}

SELECT 

FROM oa_project a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.project_stage = #{projectStage}


		AND a.contract_type = #{contractType}


		AND a.establishment_time>=#{beginEstablishmentTime} 


		AND a.establishment_time<=#{endEstablishmentTime} 


		AND a.project_money >= #{beginProjectMoney}


		AND a.project_money  <=#{endProjectMoney}


		AND a.project_no LIKE 
			'%'||#{projectNo}||'%'
			'%'+#{projectNo}+'%'
			concat('%',#{projectNo},'%')


		AND a.project_name LIKE 
			'%'||#{projectName}||'%'
			'%'+#{projectName}+'%'
			concat('%',#{projectName},'%')





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_project a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.project_stage = #{projectStage}


		AND a.contract_type = #{contractType}


		AND a.establishment_time BETWEEN #{beginEstablishmentTime} AND #{endEstablishmentTime}


		AND a.project_money >= #{beginProjectMoney}


		AND a.project_money  <=#{endProjectMoney}


		AND a.project_no LIKE 
			'%'||#{projectNo}||'%'
			'%'+#{projectNo}+'%'
			concat('%',#{projectNo},'%')


		AND a.project_name LIKE 
			'%'||#{projectName}||'%'
			'%'+#{projectName}+'%'
			concat('%',#{projectName},'%')





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM oa_project a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.project_stage = #{projectStage}


		AND a.contract_type = #{contractType}


		AND a.establishment_time BETWEEN #{beginEstablishmentTime} AND #{endEstablishmentTime}


		AND a.project_money >= #{beginProjectMoney}


		AND a.project_money  <=#{endProjectMoney}


		AND a.project_no LIKE 
			'%'||#{projectNo}||'%'
			'%'+#{projectNo}+'%'
			concat('%',#{projectNo},'%')


		AND a.project_name LIKE 
			'%'||#{projectName}||'%'
			'%'+#{projectName}+'%'
			concat('%',#{projectName},'%')


		AND (a.view_permission LIKE 
			'%"'||#{searchUserId}||'"%'
			'%"'+#{searchUserId}+'"%'
			concat('%"',#{searchUserId},'"%')
			OR 
			a.account_manager_id = #{searchUserId}
			OR
			a.business_manager_id = #{searchUserId}
			OR
			a.technical_manager_id = #{searchUserId}
			OR
			a.project_manager_id = #{searchUserId}
			OR
			a.id in (SELECT b.project_id FROM oa_project_member b where user_id=#{searchUserId})
			)




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 
	IFNULL(count(a.id) , 0) as countNum
FROM oa_project a

	a.del_flag = '0'

		AND (a.view_permission LIKE 

			concat('%"',#{searchUserId},'"%')
			OR 
			a.account_manager_id = #{searchUserId}
			OR
			a.business_manager_id = #{searchUserId}
			OR
			a.technical_manager_id = #{searchUserId}
			OR
			a.project_manager_id = #{searchUserId}
			)

SELECT 

FROM oa_project a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE oa_project SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
